import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

export default function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = input.trim();
    setInput('');
    setLoading(true);
    
    setMessages(prev => [...prev, { text: userMessage, sender: 'user' }]);

    try {
      const res = await fetch(`${API}/chatbot/query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: userMessage })
      });
      
      const data = await res.json();
      if (res.ok) {
        setMessages(prev => [...prev, {
          text: data.answer,
          sender: 'bot',
          confidence: data.confidence
        }]);
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setMessages(prev => [...prev, {
        text: "Sorry, I'm having trouble understanding that right now.",
        sender: 'bot',
        error: true
      }]);
    } finally {
      setLoading(false);
    }
  };

  // Add auto-expand functionality
  const adjustTextareaHeight = () => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = 'inherit';
      const computed = window.getComputedStyle(textarea);
      const height = textarea.scrollHeight + 
        parseInt(computed.borderTopWidth) + 
        parseInt(computed.borderBottomWidth);
      
      textarea.style.height = `${Math.min(height, 200)}px`; // Max height of 200px
    }
  };

  useEffect(() => {
    adjustTextareaHeight();
  }, [input]);

  return (
    <div className="chatbot-container">
      <div className="chat-header">
        <h2>E-Waste AI Assistant</h2>
        <p>Powered by E-waste AI</p>
      </div>
      <div className="chat-messages">
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.sender}`}>
            <div className="message-text">
              <ReactMarkdown>{msg.text}</ReactMarkdown>
            </div>
            {msg.confidence && msg.sender === 'bot' && (
              <div className="message-meta">
                Confidence: {(msg.confidence * 100).toFixed(1)}%
              </div>
            )}
          </div>
        ))}
        {loading && (
          <div className="message bot">
            <div className="typing-indicator">
              <span></span><span></span><span></span>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={sendMessage} className="chat-input">
        <textarea
          ref={textareaRef}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me anything about e-waste..."
          disabled={loading}
          rows={1}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              sendMessage(e);
            }
          }}
          style={{
            flex: 1,
            padding: '0.8rem 10rem',
            border: '1px solid #ccc',
            borderRadius: '30px',
            fontSize: '0.95rem',
            outline: 'none',
            transition: 'all 0.3s ease'
          }}
          onFocus={(e) => {
            e.target.style.borderColor = '#4e73df';
            e.target.style.boxShadow = '0 0 8px rgba(78, 115, 223, 0.3)';
          }}
          onBlur={(e) => {
            e.target.style.borderColor = '#ccc';
            e.target.style.boxShadow = 'none';
          }}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Sending...' : 'Send'}
        </button>
      </form>
    </div>
  );
}
