import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import PickupForm from './components/PickupForm';
import ConsultForm from './components/ConsultForm';
import MyRequests from './components/MyRequests';
import Chatbot from './pages/Chatbot';
import AdminDashboard from './pages/AdminDashboard';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App(){
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')||'null'));

  useEffect(()=>{
    if(token && !user){
      // decode simple stored user from localStorage when set via login/register
      const u = JSON.parse(localStorage.getItem('user')||'null');
      setUser(u);
    }
  },[token,user]);

  const logout = ()=>{ localStorage.removeItem('token'); localStorage.removeItem('user'); setToken(null); setUser(null); }

  // Protected Route Component
  const ProtectedRoute = ({ children, allowedRoles }) => {
    if (!user || !allowedRoles.includes(user.role)) {
      return <Navigate to="/" />;
    }
    return children;
  };

  if(!token) return (
    <div className="container">
      <h1>E-Waste Service</h1>
      <Register onAuth={(t,u)=>{setToken(t); setUser(u);}} />
      <hr/>
      <Login onAuth={(t,u)=>{setToken(t); setUser(u);}} />
    </div>
  );

  return (
    <BrowserRouter>
      <div className="app-container">
        <nav className="main-nav">
          {user.role === 'admin' ? (
            // Admin Navigation
            <>
              <Link to="/admin" className="nav-link">Admin Dashboard</Link>
              <button onClick={logout} className="btn-logout">Logout</button>
            </>
          ) : (
            // Regular User Navigation
            <>
              <Link to="/">Home</Link>
              <Link to="/pickup">Request Pickup</Link>
              <Link to="/consult">Expert Consultation</Link>
              <Link to="/my-requests">My Requests</Link>
              <Link to="/chatbot">AI Assistant</Link>
              <button onClick={logout}>Logout</button>
            </>
          )}
        </nav>

        <div className="main-content">
          <header>
            <h1>Welcome, {user?.name}</h1>
          </header>
          <main>
            <Routes>
              {/* Admin Routes */}
              <Route 
                path="/admin" 
                element={
                  <ProtectedRoute allowedRoles={['admin']}>
                    <AdminDashboard token={token} />
                  </ProtectedRoute>
                } 
              />

              {/* User Routes */}
              <Route 
                path="/pickup" 
                element={
                  <ProtectedRoute allowedRoles={['user']}>
                    <PickupForm token={token} api={API} />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/consult" 
                element={
                  <ProtectedRoute allowedRoles={['user']}>
                    <ConsultForm token={token} api={API} />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/my-requests" 
                element={
                  <ProtectedRoute allowedRoles={['user']}>
                    <MyRequests token={token} api={API} />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/chatbot" 
                element={
                  <ProtectedRoute allowedRoles={['user']}>
                    <Chatbot />
                  </ProtectedRoute>
                } 
              />

              {/* Default Route */}
              <Route 
                path="/" 
                element={
                  user.role === 'admin' ? 
                    <Navigate to="/admin" /> : 
                    <Navigate to="/pickup" />
                } 
              />
            </Routes>
          </main>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
