import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import PickupRequest from './pages/PickupRequest';
import ConsultRequest from './pages/ConsultRequest';
import MyRequests from './pages/MyRequests';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')||'null'));

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setToken(null);
    setUser(null);
  };

  if (!token) {
    return (
      <BrowserRouter>
        <Routes>
          <Route path="/login" element={<Login onAuth={(t,u)=>{setToken(t); setUser(u);}} />} />
          <Route path="/register" element={<Register onAuth={(t,u)=>{setToken(t); setUser(u);}} />} />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </BrowserRouter>
    );
  }

  return (
    <BrowserRouter>
      <div className="app-container">
        <nav className="main-nav">
          <div className="nav-brand">E-Waste Service</div>
          <div className="nav-links">
            <Link to="/">Dashboard</Link>
            <Link to="/pickup">Request Pickup</Link>
            <Link to="/consult">Request Consult</Link>
            <Link to="/requests">My Requests</Link>
          </div>
          <div className="nav-user">
            <span>Hello, {user?.name}</span>
            <button onClick={logout} className="btn-logout">Logout</button>
          </div>
        </nav>

        <div className="main-content">
          <Routes>
            <Route path="/" element={<Dashboard user={user} />} />
            <Route path="/pickup" element={<PickupRequest token={token} api={API} />} />
            <Route path="/consult" element={<ConsultRequest token={token} api={API} />} />
            <Route path="/requests" element={<MyRequests token={token} api={API} />} />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
