import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import PickupForm from './components/PickupForm';
import ConsultForm from './components/ConsultForm';
import MyRequests from './components/MyRequests';

const API = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App(){
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')||'null'));

  useEffect(()=>{
    if(token && !user){
      // decode simple stored user from localStorage when set via login/register
      const u = JSON.parse(localStorage.getItem('user')||'null');
      setUser(u);
    }
  },[token,user]);

  const logout = ()=>{ localStorage.removeItem('token'); localStorage.removeItem('user'); setToken(null); setUser(null); }

  if(!token) return <div className="container"><h1>E-Waste Service</h1><Register onAuth={(t,u)=>{setToken(t); setUser(u);}} /><hr/><Login onAuth={(t,u)=>{setToken(t); setUser(u);}} /></div>;

  return (
    <div className="container">
      <header>
        <h1>Welcome, {user?.name}</h1>
        <button onClick={logout}>Logout</button>
      </header>
      <main>
        <section className="card"><h2>Request Pickup</h2><PickupForm token={token} api={API} /></section>
        <section className="card"><h2>Expert Consultation</h2><ConsultForm token={token} api={API} /></section>
        <section className="card"><h2>My Requests</h2><MyRequests token={token} api={API} /></section>
      </main>
    </div>
  );
}

export default App;
